const { BT_STATES, LoopNode, SuccessLoopNode, SequenceNode, ResetSequenceNode, SelectorNode, RunningLoopNode, ExecutionNode } = bt;
class ExampleTree {
    counter = 10;
    stack = [1, 2, 3, 4, 5];
    stackElement;

    //FUNC BEGIN
    loop_task() {
        //CODE BEGIN
        console.log(this.counter--);
        return BT_STATES.SUCCESS;
        //CODE END
    };

    inner_loop_condition() {
        //CODE BEGIN
        this.stackElement = this.stack.pop();
        if (this.stackElement) {
            return BT_STATES.SUCCESS;
        }
        return BT_STATES.FAILED;
        //CODE END
    };

    inner_loop() {
        //CODE BEGIN
        console.log('Inner loop: ' + this.stackElement)
        return BT_STATES.SUCCESS;
        //CODE END
    };

    loop_post_condition() {
        //CODE BEGIN
        if (this.counter > 0) {
            return BT_STATES.SUCCESS;
        }
        return BT_STATES.FAILED;
        //CODE END
    };
    //FUNC END
    //TREE BEGIN
    tree = new LoopNode([
        new SequenceNode([
            new ExecutionNode(this.loop_task, this),
            new SelectorNode([
                new LoopNode([
                    new SequenceNode([
                        new ExecutionNode(this.inner_loop_condition, this),
                        new ExecutionNode(this.inner_loop, this)
                    ])
                ]),
                new ExecutionNode(this.loop_post_condition, this)
            ])
        ])
    ])
    //TREE END
}
const example = new ExampleTree();
console.log(example.tree.tick());
